mapper.select:
select count(*) as cnt from
Review
where shop_id = #{shopId}

select count(*) as cnt from
Review
where user_email = #{userEmail}

select Review_idx from
Review_like
where user_email = #{user_email} and shop_id = #{shopId}

select Review_idx from
Review_hate
where user_email = #{user_email} and shop_id = #{shopId}

select count(*) as cnt from
Review_Like
where review_idx = #{reviewIdx}

select count(*) as cnt from
Review_Hate
where review_idx = #{reviewIdx}
